import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { UserService } from '../../../../../Services/user';
@Component({
  selector: 'app-modal-nuevo-consulta',
  templateUrl: './modal-nuevo-consulta.component.html',
  styleUrls: ['./modal-nuevo-consulta.component.css']
})
export class ModalNuevoConsultaComponent {
  protected FechaConsulta: any;
  protected dato: any;


  constructor(private http: HttpClient , private UserService: UserService  ) { }
  //METODOS
  Mostrar(info:any){
    this.dato = info;
  }
}

    <div class="modalC">
        <div class="modalC-content">
            <div class="modalc-header">
                <h4 class="modalc-title">Crear una nueva consulta</h4>
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="black" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" (click)="mostrar = false;">
                    <line x1="18" y1="6" x2="6" y2="18" />
                    <line x1="6" y1="6" x2="18" y2="18" />
                </svg>
            </div>
            <!-- Cuerpo  -->
                <div class="modalC-container"> 
                    <div class="divFecha">
                        <label>Ingrese la fecha que desea la consulta:</label>
                        <input type="date" id="fecha" [(ngModel)]="FechaConsulta">
                    </div>
                    <textarea id="txtAreaMensaje" placeholder="Escribe el informe final aqui:" autofocus="" ></textarea>
                    <div class="botones">
                        <button>Completar la consulta</button>
                    </div>
                </div>
            </div>
        </div>
