              this.ListaExamenesFiltrada.push({
                Id: this.InfoConsultas[i],
                Nombre: this.InfoConsultas[i + 1],
                Especialidad: this.InfoConsultas[i + 2]
              });
            }
          }
        }
        this.TablaEspecialidad = true;
      });
    }
  }
