        return true;
      } else {
        alert("La fecha inicial no puede estar más lejos que la fecha final");
        return false;
      }
    }
  }
  //metodo reiniciar fechas
  ReiniciarFechas() {
    this.fechaInicial = new Date();
    this.fechaFinal = new Date();
  }
