import { Component } from '@angular/core';
import { HttpClient, HttpHeaders  } from '@angular/common/http';
import { Router } from '@angular/router';
import { UserService } from '../../../Services/user';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent {
  username: string = "";
  password: string = "";
  errorMessage: string ="";
  constructor(private http: HttpClient, private UserService: UserService  ,private router: Router) { }

  login(username: string, password: string) {
    const url = 'http://localhost:8080/Proyecto2Clinica/ControladorLogin';
    const body = new URLSearchParams();
    body.set('username', this.username);
    body.set('password', this.password);
    const options = {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    };
    this.http.post(url, body.toString(), options).subscribe(
      (response: any) => {
        if (response.valid) {
          // Si la autenticación fue exitosa, guardar la información del usuario en el servicio
          this.UserService.setUser(response.username, response.id, response.tipo);
          // Redirigir al usuario a la página de inicio
          //this.router.navigate(['/home']);
          console.error("nos largamos xd");
        } else {
          // Si la autenticación no fue exitosa, mostrar un mensaje de error
          this.errorMessage = 'Nombre de usuario o contraseña incorrectos.';
        }
      },
      (error) => {
        // Si ocurre un error al enviar la solicitud, mostrar un mensaje de error
        console.error(error);
        this.errorMessage = 'Ocurrió un error al iniciar sesión. Por favor, inténtalo de nuevo más tarde.';
      }
    );
  }

  goToRegister(){

  }
}
<div class="login-container">
  <h1>Login</h1>
  <form method="post" (submit)="login(username.value, password.value)"> 
    <label for="username">Username:</label>
    <input type="text" id="username" name="username" #username ngModel required>

    <label for="password">Password:</label>
    <input type="password" id="password" name="password" #password ngModel required>

    <div class="buttons-container">
      <button type="submit" class="login-button">Login</button>
      <button type="button" class="register-button" (click)="goToRegister()">Register</button>
    </div>
  </form>
</div>
