import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = "";
  password: string = "";

  constructor(private http: HttpClient, private router: Router) { }

  onSubmit() {
    const url = 'http://localhost:8080/login'; // URL del servlet de inicio de sesión
    const credentials = { username: this.username, password: this.password };

    this.http.post<any>(url, credentials).subscribe(response => {
      if (response.authenticated) {
        // El inicio de sesión es exitoso, redirigir al componente de inicio
        this.router.navigate(['/home']);
      } else {
        // El inicio de sesión ha fallado, mostrar un mensaje de error
        console.log('Inicio de sesión fallido');
      }
    });
  }
}
<div class="login-container">
  <h1>Login</h1>
  <form (ngSubmit)="onSubmit()"> 
    <label for="username">Username:</label>
    <input type="text" id="username" name="username" [(ngModel)]="username" required>

    <label for="password">Password:</label>
    <input type="password" id="password" name="password" [(ngModel)]="username" required>

    <div class="buttons-container">
      <button type="submit" class="login-button">Login</button>
      <button type="button" class="register-button" (click)="goToRegister()">Register</button>
    </div>
  </form>
</div>
