import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
@Component({
  selector: 'app-modal-agregar-examenes',
  templateUrl: './modal-agregar-examenes.component.html',
  styleUrls: ['./modal-agregar-examenes.component.css']
})
export class ModalAgregarExamenesComponent {
  datos: any;
  show = false;
  constructor(private http: HttpClient) {}
  showModalInicial(datos: any){
    this.datos = datos;
    this.show = true;
  }

  //metodo para mandar el archivo:}
  onFileSelected(event: Event): void {
    const archivo = (event.target as HTMLInputElement).files?.[0];
    if(archivo===undefined){
      alert("sube un archivo");
    }else{
      if (archivo.type === 'application/pdf') {
        const formData = new FormData();
        formData.append('pdfFile', archivo, archivo.name);
        this.http.post('http://example.com/upload', formData).subscribe(
          response => {
            alert('Examen subido correctamente');
          },
          error => {
            alert('Error al subir Examen:', error);
          }
        );
      } else {
        alert('Por favor seleccione un archivo PDF');
      }
    }

  }
}
