import { Component, EventEmitter, Output } from '@angular/core';
import { HttpClient} from '@angular/common/http';
@Component({
  selector: 'app-modal-agregar-examenes',
  templateUrl: './modal-agregar-examenes.component.html',
  styleUrls: ['./modal-agregar-examenes.component.css']
})
export class ModalAgregarExamenesComponent {
  datos: any;
  tabla: any;
  show = false;
  lleno = true;
  selectedFile: any;
  fileToUpload: File | undefined;
  @Output() cerrado = new EventEmitter<void>();
  constructor(private http: HttpClient) {}
//metodo de iniciacion para ver los examenes y si existe archivo
  ngOnInit() : void  {

  }
  //metodo con el que me envian el id de la solicitud
  showModalInicial(datos: any){
    this.datos = datos;
    this.show = true;
    this.getExamenesDeSolicitud().subscribe(examenes => {
      this.tabla = examenes;
    })
  }
//metodo para desactivar el botón si no está lleno:
  desactivarBoton(){
    this.lleno = false;
  }
  activarBoton(){
    this.lleno = true;
  }
  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  uploadFile(valor:any) { 
    if (this.selectedFile.type === 'application/pdf') { 
      const formData = new FormData();
      formData.append('file', this.selectedFile);
      this.http.put('http://localhost:8080/Proyecto2Clinica/ControladorExamenesSolicitudes?accion=subirExamenPdf&IdESL='+valor, formData).subscribe(
        res => {
          alert('Examen subido correctamente');
          this.getExamenesDeSolicitud().subscribe(examenes => {
            this.tabla = examenes;
          })
        },
        err => {
          alert('Error al subir Examen:'+err);
        }
      );
    }else {
      alert('Por favor seleccione un archivo PDF');
    }
  }

//borrar

  //metodo que busca los examenes:
  getExamenesDeSolicitud(){
    const url = 'http://localhost:8080/Proyecto2Clinica/ControladorExamenesSolicitudes?accion=obtenerExamenesSolicitud&IdSolicitud='+this.datos;
    return this.http.get<any[]>(url);
  }
  //metodo para finalizar la consulta cuando este lleno todo:
    FinalizarConsulta(){
      this.http.put('http://localhost:8080/Proyecto2Clinica/ControladorSolicitudes?accion=FinalizarSolicitud&IdSolicitud='+this.datos, this.datos).subscribe(
        res => {
          alert('Se completo la solicitud correctamente');
          this.getExamenesDeSolicitud().subscribe(examenes => {
            this.tabla = examenes;
            this.cerrado.emit();
            this.show = false;
          })
        },
        err => {
          if(err==409){
            alert('ocurrio un error con la finalización de la solicitud, comuniquese con soporte tecnico');
          }else{ alert('ocurrio un error con el deposito, comuniquese con soporte tecnico para solucionarlo');}

        }
      );
    }
}
<ng-container *ngIf="show">
    <div class="modalC">
        <div class="modalC-content">
            <div class="modalc-header">
                <h4 class="modalc-title">
                    Solicitud de examen:
                </h4>
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="black" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" (click)="show = false;">
                        <line x1="18" y1="6" x2="6" y2="18" />
                        <line x1="6" y1="6" x2="18" y2="18" />
                    </svg>
            </div>
            <!-- Cuerpo si la consulta es pendiente: -->
                <div class="modalC-container"> 
                    <table>
                        <tr>
                            <td>Nombre del examen:</td>
                            <td></td>
                            <td>Subir</td>
                          </tr>
                        <ng-container *ngFor="let dato of tabla; let i = index;">
                        <ng-container *ngIf="i % 3 === 0">
                            <tr>
                              <ng-container *ngFor="let columna of [0, 1, 2]; let j = index;">
                                <td>{{ j === 0 ? tabla[i + j] : '' }}</td>
                              </ng-container>
                              <td>
                                <input *ngIf="tabla[i + 1].includes('No')" type="file" (change)="onFileSelected($event)">
                                <div  *ngIf="tabla[i + 1].includes('No'); then cumple else noCumple"></div>
                                <button *ngIf="tabla[i + 1] == 'No'" (click)="uploadFile(tabla[i+2])">Upload</button>
                                <label *ngIf="tabla[i + 1] !== 'No'">{{ tabla[i+1]}}</label>
                                <ng-template #cumple>
                                  {{desactivarBoton()}}
                                </ng-template>
                                <ng-template #noCumple>
                                  {{activarBoton()}}
                                </ng-template>
                              </td>
                            </tr>
                          </ng-container>
                        </ng-container>
                      </table>
                    <div class="botones">
                        <button (click)="FinalizarConsulta()" *ngIf="lleno" >Finalizar solicitud</button>
                    </div>
                </div>
            </div>
        </div>      
</ng-container>
