import { Component } from '@angular/core';

@Component({
  selector: 'app-medico-menu-especialidades',
  templateUrl: './medico-menu-especialidades.component.html',
  styleUrls: ['./medico-menu-especialidades.component.css']
})
export class MedicoMenuEspecialidadesComponent implements OnInit  { 
  selectedEspecialidad: Especialidad;
  precio: number;
  especialidades: Especialidad[];
  especialidadesAgregadas: Especialidad[];

  constructor(private especialidadService: EspecialidadService) { }

  ngOnInit(): void {
    this.especialidadService.getEspecialidades().subscribe(especialidades => {
      this.especialidades = especialidades;
    });

    this.especialidadService.getEspecialidadesAgregadas().subscribe(especialidadesAgregadas => {
      this.especialidadesAgregadas = especialidadesAgregadas;
    });
  }

  submitEspecialidad(): void {
    this.especialidadService.agregarEspecialidad(this.selectedEspecialidad.id, this.precio).subscribe(especialidad => {
      this.especialidadesAgregadas.push(especialidad);
    });
  }

  editarEspecialidad(especialidad: Especialidad): void {
    // abrir un modal o componente para editar la especialidad
  }
}
