    return this.http.get(url);
  }
  //metodo para conseguir las getExamenesPendientes:
  getExamenesPendientes() {
    const url = 'http://localhost:8080/Proyecto2Clinica/ControladorDeExamenes?accion=obtenerExamenesPendientes';
    return this.http.get(url);
  }
  //cambiar tipo de examen:
  CambiarEstado(id, Estado) {
    const Id = id;
    const estado = Estado;
    const body = {
      estado: estado
    };
    this.http.put(`http://localhost:8080/Proyecto2Clinica/ControladorExamenesLaboratorios?accion=ActualizarEstadoAdmin&IdEL=${Id}&Estado=${estado}`, body).subscribe(response => {
      alert('Se cambió el estado de la solicitud correctamente.');
      // Actualizar la lista de especialidades agregadas
      this.TablaAsignaciones();
    }, error => {
      if (error.status === 400) {
        alert('Error al cambiar el estado de la solicitud: el ID ingresado no es válido.');
      } else {
        alert('Error al editar la exámen');
      }
    });
  }
  //aceptar nuevo tipo de examen:
  aceptarExamen(id) {
    const Id = id;
    const body = {
      Id: id
    };
    this.http.put(`http://localhost:8080/Proyecto2Clinica/ControladorDeExamenes?accion=ActualizarEstadoAdmin&IdEL=${Id}`, body).subscribe(response => {
      alert('Se confirmo el nuevo exámen correctamente.');
      // Actualizar la lista de especialidades agregadas
      this.TablaDeExamanes();
    }, error => {
      if (error.status === 400) {
        alert('Error al  confirmar el nuevo exámen.');
      } else {
        alert('Error al  confirmar el nuevo exámen');
      }
    });
  }
  //eliminar nuevo tipo de examen:
  eliminarExamen(id) {
    const Id = id;
    this.http.delete(`http://localhost:8080/Proyecto2Clinica/ControladorDeExamenes?accion=ActualizarEstadoAdmin&IdEL=${Id}`).subscribe(response => {
      alert('Se eliminó la solicitud correctamente.');
      // Actualizar la lista de especialidades agregadas
      this.TablaDeExamanes();
    }, error => {
      if (error.status === 400) {
        alert('Error al eliminó la solicitud correctamente.');
      } else {
        alert('Error al eliminó la solicitud correctamente.');
      }
    });
  }
}
