import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { NgForm } from '@angular/forms';
import { Observable } from 'rxjs';
@Component({
  selector: 'app-home-admin',
  templateUrl: './home-admin.component.html',
  styleUrls: ['./home-admin.component.css']
})
export class HomeAdminComponent {
    //Variables para carga de datos:
    protected MostrarCargaDeDatos = true;
    protected ArchivoDeCarga: any;
    //constructor
    constructor(private http: HttpClient) {}
  //metodo para la carga de datos:
  onUpload(): void {
    const formData = new FormData();
    formData.append('file', this.ArchivoDeCarga);

    this.http.post('http://localhost:8080/Proyecto2Clinica/ControladorCargaDeDatos', formData).subscribe(
      (response) => console.log(response),
      (error) => console.log(error)
    );
  }
  SubirArchivo(event: any) {
    if(!(this.ArchivoDeCarga==undefined)) {
      event.preventDefault();
      const formData = new FormData();
      const headers = new HttpHeaders().set('Content-Type', 'application/json');
      formData.append('file', this.ArchivoDeCarga);
      this.http.post('http://localhost:8080/Proyecto2Clinica/ControladorCargaDeDatos', formData, { headers }).subscribe(
        (response) => console.log('Archivo cargado exitosamente'),
        (error) => console.error('Error al cargar el archivo', error)
      );
    }else{ alert("seleccione un archivo")}

  }
  onFileSelected(event:any) {
    const file: File = event.target.files[0];
    this.ArchivoDeCarga = file;
    console.log(file);
  }
}
<!DOCTYPE html>
<html>
    <head>
        <meta http-equiv="Content-Type" content="text/html; charset=UTF-8">
        <meta http-equiv="X-UA-Compatible" content="IE=edge">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Administración</title>
        <link rel="stylesheet" href="home-admin.component.css">
        <link rel="preconnect" href="https://fonts.googleapis.com">
        <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
        <link href="https://fonts.googleapis.com/css2?family=Caveat+Brush&display=swap" rel="stylesheet">
    </head>
    <body>
        <section>
        <header style="z-index: 4;">
            <div class="contenedorGrande">
                <div class="contenedor">
                    <div class="nav-link" id="menuReportes">
                        <img src="https://cdn-icons-png.flaticon.com/512/4701/4701237.png" width="60" height="60">
                        <label>Reportes</label>
                    </div>
                    <a  class="nav-link">
                        <img src="https://cdn-icons-png.flaticon.com/512/8890/8890577.png" width="60" height="60">
                        <label>Menu Asignar Examen</label>
                    </a>
                </div>
                <div class="contenedor">
                    <a id="logo">Administración</a>
                </div>
                <div class="contenedor">
                    <a  class="nav-link">
                        <img src="https://cdn-icons-png.flaticon.com/512/2000/2000842.png" width="60" height="60">
                        <label>Menu Solicitudes</label>
                    </a>
                    <a class="nav-link" href="../Login.jsp?cerrar=true">
                        <img src="https://cdn-icons-png.flaticon.com/512/2574/2574200.png" width="60" height="60">
                        <label>Cerrar Sesión</label>
                    </a>
                </div>
            </div>
        </header>
    </section>
        <section id="sectionLibro">
            <div class="contenedorPrincipal">
                <div *ngIf="MostrarCargaDeDatos">
                    <input type="file" #fileInput (change)="onFileSelected($event)">
                    <button (click)="onUpload()">Enviar</button>
                </div>
            </div>
        </section>
    </body>
</html>
