import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-menu-solicitar-examenes-admin',
  templateUrl: './menu-solicitar-examenes-admin.component.html',
  styleUrls: ['./menu-solicitar-examenes-admin.component.css']
})
export class MenuSolicitarExamenesAdminComponent {
  protected DataTabla: any[]=[];

  constructor(private http: HttpClient) { }

    TablaAsignaciones(){
      this.getExamenes().subscribe(info => {
        this.DataTabla = info;
        console.log(this.DataTabla);
      })
    }
    //metodo para conseguir las solicitude de examenes:
    getExamenes() {
      const url = 'http://localhost:8080/Proyecto2Clinica/ControladorDeExamenes?accion=obtenerSolicitudesExamenes';
      return this.http.get<any[]>(url);
    }
    //cambiar tipo de examen:
    CambiarEstado(id :any , Estado:any){
      const Id = id;
      const estado = Estado;
      const body = { estado: estado };
      this.http.put(`http://localhost:8080/Proyecto2Clinica/ControladorExamenesLaboratorios?accion=ActualizarEstadoAdmin&IdEL=${Id}&Estado=${estado}`, body).subscribe(
        response => {
          alert('examen editado correctamente.');
          // Actualizar la lista de especialidades agregadas
        },
        error => {
          if (error.status === 400) {
            alert('Error al editar el exámen: el ID ingresado no es válido.');
          } else {
            alert('Error al editar la exámen');
          }
        }
      );
    }
}
