import { Component, EventEmitter, Output } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { UserService } from '../../../../../Services/user';
@Component({
  selector: 'app-modal-agregar-examenes-solicitud-pacientes',
  templateUrl: './modal-agregar-examenes-solicitud-pacientes.component.html',
  styleUrls: ['./modal-agregar-examenes-solicitud-pacientes.component.css']
})
export class ModalAgregarExamenesSolicitudPacientesComponent {
  @Output() cerrado = new EventEmitter();
  datos: any;
  public chow = false;
  examenes: any;
  public selectedOption: any;
  ExamenesAgregados: any;
  MensajeDeConfirmacion = false;
  constructor(private http: HttpClient, private UserService:UserService  ) { }
  ngOnInit() : void  {

  }
  Mostrar(datos: any){
    this.datos = datos;
    this.chow = true;
    this.subscribirse();

  }
  subscribirse(){
    this.getExamenes().subscribe(examen => {
      this.examenes = examen;
    });
    this.getExamenesAgregados().subscribe(examen => {
      this.ExamenesAgregados = examen;
    })
  }
  getExamenes() {
    const url = 'http://localhost:8080/Proyecto2Clinica/ControladorExamenesLaboratorios?accion=obtenerExamenes&IdLab='+this.datos.Id;
    return this.http.get<any[]>(url);
  }
  getExamenesAgregados(){
    const url = 'http://localhost:8080/Proyecto2Clinica/ControladorExamenesConsultas?accion=obtenerExamenesAgregados&id='+this.datos.IdConsultas;
    return this.http.get<any[]>(url);
  }
  //Crear la solicitud:
  CrearSolicitudIncompleta(){
    const Idlab = this.datos.Id;
    const idUsuario = this.UserService.getId();
    const body = {Nombre: this.selectedOption};
    this.http.post(`http://localhost:8080/Proyecto2Clinica/ControladorExamenesConsultas?accion=CrearExamenAsignado&NombreExamen=${nombre}&IdConsulta=${this.datos.IdConsultas}`, body).subscribe(
      response => {
        alert('Se agrego el examen a la consulta');
        this.subscribirse();
        // Actualizar la lista de especialidades agregadas
      },
      error => {
        if (error.status === 400) {
          alert('No se encontro el examen pedido');
        } else if(error.status === 409) {
          alert('No puedes agregar el mismo examen dos veces ');
        }
      }
    );
  }
  AgregarExamenSeleccionado(){
    const nombre = this.selectedOption;
    const body = {Nombre: this.selectedOption};
    this.http.post(`http://localhost:8080/Proyecto2Clinica/ControladorExamenesConsultas?accion=CrearExamenAsignado&NombreExamen=${nombre}&IdConsulta=${this.datos.IdConsultas}`, body).subscribe(
      response => {
        alert('Se agrego el examen a la consulta');
        this.subscribirse();
        // Actualizar la lista de especialidades agregadas
      },
      error => {
        if (error.status === 400) {
          alert('No se encontro el examen pedido');
        } else if(error.status === 409) {
          alert('No puedes agregar el mismo examen dos veces ');
        }
      }
    );
  }
  EliminarExamenesSolicitados(e: any){
    this.http.delete('http://localhost:8080/Proyecto2Clinica/ControladorExamenesConsultas?accion=obtenerExamenesAgregados&Nombre='+e+'&IdConsulta='+this.datos.IdConsultas).subscribe(
      (response) => {
        alert('Se elimino el examen de la consulta con exito');
      },
      (error) => {
        this.subscribirse();
        alert('Se elimino el examen de la consulta con exito');
      }
    );
  }
  Finalizar(){
    if(this.MensajeDeConfirmacion){
      this.chow = false;

      const idConsulta = this.datos.IdConsultas;
      const body = { idConsulta: idConsulta };
      this.http.put(`http://localhost:8080/Proyecto2Clinica/ControladorConsultas?accion=ActualizarConsulta&IdConsulta=${idConsulta}&Estado=EXAMEN_PENDIENTE`, body).subscribe(
        response => {
          alert('Se cambio la consulta a estado examen pendiente');
          this.cerrado.emit();
        },
        error => {
          if (error.status === 400) {
            alert('Error al actualizar la consulta');
          } else {
            alert('Error al actualizar');
          }
        }
      );
    }else{
      alert("Recuerda que luego no podras agregar más examenes a la consulta, si ya acabaste presiona finalizar otra vez");
      this.MensajeDeConfirmacion = true;
    }
  }
}

<ng-container *ngIf="chow">
    <div class="modalC">
        <div class="modalC-content">
            <div class="modalc-header">
                <h4 class="modalc-title">
                    Agregar Examenes a la Solicitud a: {{ datos.Nombre }}
                </h4>
            </div>
            <!-- Cuerpo  -->
                <div class="modalC-container"> 
                    <label for="especialidad">Selecione un examen para agregar</label>
                    <select name="selectedOption" [(ngModel)]="selectedOption">
                        <option *ngFor="let examenes of examenes" [value]="examenes.nombreExamen">{{examenes.nombreExamen}}</option>
                    </select>
                    <button (click)=" AgregarExamenSeleccionado()">Agregar Examen seleccionado</button>
                    <table>
                        <thead>
                          <tr>
                            <th>Examenes Agregados:</th>
                            <th>Acción</th>
                          </tr>
                        </thead>
                        <tbody>
                          <tr *ngFor="let item of ExamenesAgregados">
                            <td>{{ item }}</td>
                            <td><button (click)="EliminarExamenesSolicitados(item)"> Eliminar Examen</button></td>
                          </tr>
                        </tbody>
                      </table>
                    <div class="botones">
                        <button (click)="Finalizar()">Finalizar</button>
                    </div>
                </div>
            </div>
        </div>          
</ng-container>
